import React from 'react';
import classnames from 'classnames';

import useFetch from 'hooks/useFetch';

export const AddToFavorites = ({
  isFavorited,
  favoritesCount,
  articleSlug,
}) => {
  const apiUrl = `/articles/${articleSlug}/favorite`;
  const [{ response }, doFetch] = useFetch(apiUrl);
  const favoritesWithResponse = response ? response.article.favoritesCount : favoritesCount;
  const isFavoritedResponse = response ? response.article.favorited : isFavorited;

  const classes = classnames({
    btn: true,
    'btn-sm': true,
    'btn-primary': isFavoritedResponse,
    'btn-outline-primary': !isFavoritedResponse,
  });

  const handleLike = event => {
    event.preventDefault();
    doFetch({
      method: isFavoritedResponse ? 'delete' : 'post'
    });
  }

  return (
    <button className={classes} onClick={handleLike}>
      <i className="ion-heart"></i>
      <span>&nbsp; { favoritesWithResponse }</span>
    </button>
  );
};