import React, { useContext } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { CurrentUserContext } from 'contexts/currentUser';

export const TopBar = () => {
  const [ { currentUser, isLoading, isLoggedIn } ] = useContext(CurrentUserContext);

  return (
    <nav className='navbar navbar-light'>
      <div className="container">
        <Link to='/' className='navbar-brand'>Medium</Link>
        <ul className="nav navbar-nav pull-xs-right">
          <li className="nav-item">
            <NavLink
              to="/"
              className="nav-link"
              exact
              activeClassName='active'
            >
              Home
            </NavLink>
          </li>
        { !isLoading && !isLoggedIn && (
          <>
            <li className="nav-item">
              <NavLink
                to="/login"
                className="nav-link"
                exact
                activeClassName='active'
              >
                Sign in
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                to="/register"
                className="nav-link"
                exact
                activeClassName='active'
              >
                Sign up
              </NavLink>
            </li>
          </>
        )}
        { !isLoading && isLoggedIn && (
          <>
            <li className="nav-item">
              <NavLink
                to="/articles/new"
                className="nav-link"
                exact
                activeClassName='active'
              >
                <i className='ion-compose'></i>
                &nbsp; New Post
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                to={`/profiles/${currentUser.username}`}
                className="nav-link"
                exact
                activeClassName='active'
              >
                <img
                  src={currentUser.image}
                  className="user-pic"
                  alt={currentUser.username}
                />
                &nbsp;
                { currentUser.username }
              </NavLink>
            </li>
          </>
        ) }
        </ul>
      </div>
    </nav>
  );
};