import React, { useEffect, useContext, useState } from 'react';
import { pathOr } from 'ramda';
import { Link, Redirect } from 'react-router-dom';
import moment from 'moment';

import useFetch from 'hooks/useFetch';
import { Loading } from 'components/loading';
import { ErrorMessage } from 'components/error-message';
import { TagList } from 'components/tag-list';
import { CurrentUserContext } from 'contexts/currentUser';

export const Article = ({ match }) => {
  const slug = pathOr('', ['params', 'slug'], match);
  const apiUrl = `/articles/${slug}`;
  const [{ response, error, isLoading }, doFetch] = useFetch(apiUrl);
  const [{ response: deleteArticleResponse, }, doDeleteArticle] = useFetch(apiUrl);
  const [user] = useContext(CurrentUserContext);
  const [isDeleted, setIsDeleted] = useState(false);

  const isAuthor = () => {
    if ([
      !response,
      !user.isLoggedIn,
    ].some(Boolean)) {
      return false;
    }

    return response.article.author.username === user.currentUser.username;
  }

  const deleteArticle = article => {
    doDeleteArticle({
      method: 'delete',
    });
  }

  useEffect(() => {
    doFetch();
  }, [slug, doFetch]);

  useEffect(() => {
    if (!deleteArticleResponse) {
      return;
    }

    setIsDeleted(true);
  }, [deleteArticleResponse, setIsDeleted]);

  if (isDeleted) {
    return <Redirect to='/feed' />
  }

  return (
    <>
      { isLoading && (<Loading />) }
      { error && (<ErrorMessage />) }

       { !isLoading && response && (
        <> 
        <div className="article-page">
          <div className="banner">
              <div className="container">
                <h1>{ response.article.title }</h1>
                <div className="article-meta">
                  <Link to={`/profiles/${response.article.author.username}`}>
                    <img src={response.article.author.image} alt=""/>
                  </Link>
                  <div className="info">
                    <Link to={`/profiles/${response.article.author.username}`}>
                      { response.article.author.username }
                    </Link>
                    <span className="date">
                      { moment(response.article.createdAt).format('DD.MM.YYYY hh:mm') }
                    </span>
                  </div>
                  { isAuthor() && (
                    <span>
                      <Link
                        to={`/articles/${slug}/edit`}
                        className='btn btn-outline-secondary btn-sm'
                      >
                        <i className='ion-edit'></i>
                        &nbsp;
                        Edit article
                      </Link>
                      &nbsp;&nbsp;
                      <button 
                        className='btn btn-outline-danger btn-sm'
                        onClick={deleteArticle}
                      >
                        <i className="ion-trash-a"></i>
                        &nbsp;
                        Delete article
                      </button>
                    </span>
                  ) }
                </div>
              </div>
            
          </div>
        </div>
        <div className="container page">
          <div className="row article-content">
            <div className="col-xs-12">
              <div>
                <p>{ response.article.body }</p>
              </div>
              <TagList tags={response.article.tagList} />
            </div>
          </div>
        </div>
        </>
      ) }
    </>
  );
};
