import React, { useEffect } from 'react';
import { stringify } from 'query-string';
import { pathOr } from 'ramda';

import useFetch from 'hooks/useFetch';
import { getPaginator, limit } from 'utils';

import { Feed } from 'components/feed';
import { Pagination } from 'components/pagination';
import { PopularTags } from 'components/popular-tags';
import { Loading } from 'components/loading';
import { ErrorMessage } from 'components/error-message';
import { FeedToggler } from 'components/feed-toggler';

export const GlobalFeed = ({ location, match }) => {
  const { offset, currentPage } = getPaginator(location.search);
  const url = match.url;
  const stringifedParams = stringify({
    limit,
    offset
  });
  const apiUrl = `/articles?${stringifedParams}`;
  const [{ response, isLoading, error }, doFetch] = useFetch(apiUrl);
  const total = pathOr(0, ['articlesCount'], response);

  useEffect(() => {
    doFetch();
  }, [doFetch, currentPage]);

  return (
    <div className="home-page">

      <div className="banner">
        <div className="container">
          <h1>Medium clone</h1>
          <p>A place to share knowledge</p>
        </div>
      </div>

      <div className="container page">
        <div className="row">
          <div className="col-md-9">
            <FeedToggler />
            { isLoading && <Loading /> }
            { error && <ErrorMessage /> }
            { !isLoading && response && (
              <>
              <Feed articles={response.articles} />
              <Pagination total={total} limit={limit} url={url} currentPage={currentPage} />
              </>
            ) }
          </div>

          <div className="col-md-3">
            <PopularTags />
          </div>

        </div>
      </div>
    </div>
  );
};
