import React, { useEffect } from 'react';
import { pathOr } from 'ramda';
import { NavLink } from 'react-router-dom';

import useFetch from 'hooks/useFetch';
import { Loading } from 'components/loading';
import { UserArticles } from 'components/user-articles';

export const UserProfile = ({ match, location }) => {
  const slug = pathOr('', ['params', 'slug'], match);
  const isFavorites = location.pathname.includes('favorites');

  const apiUrl = `/profiles/${slug}`;
  const [{ response, isLoading }, doFetch] = useFetch(apiUrl);
  
  useEffect(() => {
    doFetch();
  }, [doFetch,]);

  if (isLoading) {
    return <Loading />
  }

  if (!response) {
    return null;
  }

  return (
    <div className='profile-page'>
      <div className='user-info'>
        <div className='container'>
          <div className='row'>
            <div className='col-xs-12 col-md-10 offset-md-1'>
              <img
                className='user-img'
                src={ response.profile.image }
                alt=''
              />
              <h4>{ response.profile.username }</h4>
              <p>{ response.profile.bio }</p>
            </div>
          </div>
        </div>
      </div>
      <div className='container'>
        <div className='row'>
          <div className="col-xs-12 col-md-10 offset-md-1">
            <div className='articles-toggle'>
              <ul className='nav nav-pills outline-active'>
                <li className='nav-item'>
                  <NavLink className='nav-link' to={`/profiles/${response.profile.username}`} exact>
                    My Posts
                  </NavLink>
                </li>
                <li className='nav-item'>
                  <NavLink className='nav-link' to={`/profiles/${response.profile.username}/favorites`} exact>
                    Favorites Posts
                  </NavLink>
                </li>
              </ul>
            </div>
            <UserArticles
              username={response.profile.username}
              location={location}
              isFavorites={isFavorites}
              url={location.pathname}
            />
          </div>
        </div>
      </div>
    </div>
  );
}